    <script>
        $('#newsletter-form').on('submit', function(e) {
            e.preventDefault();

            let email = $('#newsletter-email').val();
            let messageBox = $('#newsletter-message');

            $.ajax({
                url: '{{ route('newsletter.subscribe') }}',
                type: 'POST',
                data: {
                    _token: '{{ csrf_token() }}',
                    email: email
                },
                success: function(response) {
                    messageBox.text(response.success).css('color', 'limegreen');
                    $('#newsletter-email').val('');
                },
                error: function(xhr) {
                    let errors = xhr.responseJSON.errors;
                    if (errors && errors.email) {
                        messageBox.text(errors.email[0]).css('color', 'red');
                    } else {
                        messageBox.text('Something went wrong.').css('color', 'red');
                    }
                }
            });
        });
    </script>

